  prepFisica_title: "Training for Dancers in Barcelona | Specialized Training | Farray's Center",
  prepFisica_description:
    'Specific training for dancers in Barcelona. Strength, flexibility, injury prevention and performance improvement. Training adapted to professional dancer needs.',
  prepFisica_breadcrumb_home: 'Home',
  prepFisica_breadcrumb_classes: 'Dance Classes in Barcelona',
