  danzaBarcelona_whichStyle_expression_goal: 'Expresión libre y creatividad artística',
  danzaBarcelona_whichStyle_expression_style: 'Danza Contemporánea',
  danzaBarcelona_whichStyle_expression_why:
    'Fusiona técnica clásica con libertad creativa. Trabajo de suelo, improvisación, contacto y desarrollo de tu lenguaje artístico único.',
  danzaBarcelona_whichStyle_energy_goal: 'Energía, musicalidad y dinamismo',
  danzaBarcelona_whichStyle_energy_style: 'Modern Jazz',
  danzaBarcelona_whichStyle_energy_why:
    'Combina ballet con ritmos contemporáneos. Isolaciones, turns, saltos y coreografías dinámicas. Ideal para musicals, shows y actuaciones.',
  danzaBarcelona_whichStyle_cultural_goal: 'Conexión cultural africana y ritmo',
